sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
		this.rightnow = new GlideDateTime();
	},
    isDatePast: function(strDate) {
        // Create GlideDateTime objects for the current date and the passed in date
        var testdate = new GlideDateTime(strDate);
        var isDatePast = false;

        // If the testdate is before rightnow, return true
        if (testdate.before(this.rightnow)) {
            isDatePast = true;
        }
        return isDatePast;
    },
    isDateToday: function(strDate) {
        // Create GlideDateTime objects for the current date and the passed in date
        var testdate = new GlideDateTime(strDate);

		var today = this.rightnow.getLocalDate();
		var date = testdate.getLocalDate();
        var isDateToday = false;

        // If the date is rightnow, return true
        if (today.compareTo(date) == 0) {
            isDateToday = true;
        }
        return isDateToday;
    },
    type: 'NeedItUtils'
};
